    //再次优化，当子字符串所包含的某一个单词数量超过了words数组里的该单词的数量时，不需要全部重新统计
    public static List<Integer> findSubstring(String s, String[] words) {
        List<Integer> result = new ArrayList<>();
        if(words==null||words.length<0)return result;
        int n = words[0].length();
        if(s==null||s.length()<n)return result;
        Map<String,Integer> map1 =new HashMap<>();
        Map<String,Integer> map2 =new HashMap<>();
        for(int i=0;i<words.length;i++){
            if(map1.containsKey(words[i])){
                map1.put(words[i],map1.get(words[i])+1);
            }else{
                map1.put(words[i],1);
            }
        }
        for(int i=0;i<=s.length()-n;i++){
            int left=i;
            int right=i+n;
            while(right<=s.length()){
                String str = s.substring(left, right);
                if(map1.containsKey(str)){
                    int count = map2.get(str)==null?0:map2.get(str);
                    if(count==map1.get(str)){
                        break;
                    }
                    map2.put(str,count+1);
                }else{
                    break;
                }
                left+=n;
                right+=n;
            }
            boolean can = true;
            for (String key : map1.keySet()) {
                if (!map2.containsKey(key) || !map2.get(key).equals(map1.get(key))) {
                    can = false;
                    break;
                }
            }
            if(can)result.add(i);
            map2.clear();
        }
        return result;
    }
